import React from "react";
import "./App.css";
import { useState } from "react";

function App() {
  const col = "yellow";
  const [bg, setBg] = useState(col);
  const bgChange = () => {
    let newBg = "red";
    setBg(newBg);
  };

  return (
    <div style={{ backgroundColor: bg }}>
      <button onClick={bgChange} type="button">
        click me
      </button>
    </div>
  );
}

export default App;
