import React, { useState, useEffect } from "react";
import Axios from "axios";
import { Link } from "react-router-dom";
import { Users, Lock, Anchor, Clipboard } from "react-feather";
const Todo = () => {
  const [resDetail, setResDetail] = useState({});

  const onSubmitDetails = () => {
    if (!details) return seterror("empty field");
    Axios.post("http://localhost:8080/todo/create", {
      details: details,
      user_id: window.localStorage.getItem("user_id"),
    }).then((res) => {
      console.log(res.data.data);
      setResDetail(res.data.data);
    });
  };
  const [details, setDetails] = useState([]);
  const [error, seterror] = useState("");

  return (
    <>
      <div className="container-fluid">
        <div className="row ">
          <div className="col-xl-4"></div>
          <div className="col-xl-4">
            <div className="card mt-3">
              <div className="card-header">
                {" "}
                <h1>
                  <Clipboard
                    size="30"
                    class="mr-2"
                    style={{ verticalAlign: "baseline" }}
                  />
                  The Things You Do
                  <Anchor
                    size="30"
                    class="mr-3"
                    style={{ verticalAlign: "baseline" }}
                  />
                </h1>
              </div>
              <input
                type="srting "
                placeholder="What To Do"
                onChange={(e) => setDetails(e.target.value)}
              ></input>
              <button
                onClick={onSubmitDetails}
                className="btn btn-outline bg-primary"
              >
                Click
              </button>
            </div>
            {/* {resDetail ? (
              <div className="alert alert-info">{resDetail.details}</div>
            ) : null} */}
            <table class="table border shadow">
              <thead class="thead-dark" />
              {/* {details.map((users, index) => ( */}
              <tbody>
                <th scope="row"></th>
                <tr>
                  <td>{resDetail.details}</td>
                  <div class="mr-5">
                    <Link class="btn btn-outline-primary mr-2">Edit</Link>
                    <Link class="btn btn-danger">Delete</Link>
                  </div>
                </tr>
              </tbody>
              {/* ))} */}
            </table>
          </div>
        </div>
      </div>
    </>
  );
};

export default Todo;
