import React, { useState } from "react";
const App = () => {
  const [name, setName] = useState();
  const [okClick, fullName] = useState();

  const InputEvent = (event) => {
    console.log(event.target.value);
    setName(event.target.value);
  };
  const onSubmit = () => {
    fullName = { name };
  };

  return (
    <div>
      <h1>hello {okClick}</h1>
      <input
        type="text"
        placeholder="enter somthing"
        onChange={InputEvent}
        value={name}
      />
      <br></br>
      <button onClick={okClick}>click me</button>
    </div>
  );
};
export default App;
