import React, { useState } from "react";

const App = () => {
  var time = new Date().toLocaleTimeString();
  const [state, currState] = useState(time);

  //var time = new Date().toLocaleTimeString();

  const currentTime = () => {
    currState();
  };
  return (
    <div>
      <h1>{time}</h1>
      <button onClick={currentTime}>refresh time</button>
    </div>
  );
};
export default App;
