import React, { useState, useEffect } from "react";
import Axios from "axios";
import { Link } from "react-router-dom";
import { Anchor, Clipboard } from "react-feather";
const Todo = () => {
  const [resDetail, setResDetail] = useState({});

  const onSubmitDetails = () => {
    if (!details) return seterror("empty field");
    Axios.post("http://localhost:8080/todo/create", {
      details: details,
      user_id: window.localStorage.getItem("user_id"),
    }).then((res) => {
      console.log(res.data.data);
      setResDetail(res.data.data);
    });
  };
  const [details, setDetails] = useState("");
  const [error, seterror] = useState("");

  return (
    <>
      <div className="container-fluid">
        <div className="row ">
          <div className="col-xl-4"></div>
          <div className="col-xl-4">
            <div className="card mt-3">
              <div className="card-header">
                {" "}
                <h1>
                  <Clipboard
                    size="30"
                    class="mr-2"
                    style={{ verticalAlign: "baseline" }}
                  />
                  Things You Do
                  <Anchor
                    size="30"
                    class="mr-4"
                    style={{ verticalAlign: "baseline" }}
                  />
                </h1>
              </div>
              <input
                type="srting "
                className="pl-2 border rounded"
                style={{ height: "40px", outline: "none" }}
                placeholder="What To Do"
                onChange={(e) => setDetails(e.target.value)}
              ></input>
              <button
                onClick={onSubmitDetails}
                className="btn btn-outline bg-primary mt-4"
              >
                Click
              </button>
            </div>
            {/* {resDetail ? (
              <div className="alert alert-info">{resDetail.details}</div>
            ) : null} */}
            {resDetail.details ? (
              <table class="table border shadow">
                <thead class="thead-dark" />
                <tbody>
                  <th scope="row"></th>
                  <tr>
                    <td>{resDetail.details}</td>
                    <div class="mr-5">
                      <Link class="btn btn-outline-primary mr-2">Edit</Link>
                      <Link class="btn btn-danger">Delete</Link>
                    </div>
                  </tr>
                </tbody>
              </table>
            ) : null}
          </div>
        </div>
      </div>
    </>
  );
};

export default Todo;
