import React, { useState } from "react";
import Axios from "axios";
import { Link } from "react-router-dom";
const Todo = () => {
  const [resDetail, setResDetail] = useState({});

  const onSubmitDetails = () => {
    if (!details) return seterror("empty field");
    Axios.post("http://localhost:8080/todo/create", {
      details: details,
      user_id: window.localStorage.getItem("user_id"),
    }).then((res) => {
      console.log(res.data.data);
      setResDetail(res.data.data);
    });
  };
  const [details, setDetails] = useState("");
  const [error, seterror] = useState("");

  return (
    <>
      <div className="container-fluid">
        <div className="row ">
          <div className="col-xl-4"></div>
          <div className="col-xl-4">
            <div className="card mt-3">
              <div className="card-header"></div>
              <input
                type="srting "
                placeholder="What To Do"
                onChange={(e) => setDetails(e.target.value)}
              ></input>
              <button
                onClick={onSubmitDetails}
                className="btn btn-outline bg-primary"
              >
                Click
              </button>
            </div>
          </div>
          <table class="table border shadow">
            <thead class="thead-dark">
              <tr>
                <th scope="col">to do</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {setDetails.map((user, index) => (
                <tr>
                  <th scope="row">{index + 1}</th>
                  <td>{resDetail.details}</td>
                  <td>
                    <Link
                      class="btn btn-primary mr-2"
                      to={`/users/${user.details}`}
                    >
                      View
                    </Link>
                    <Link
                      class="btn btn-outline-primary mr-2"
                      to={`/users/edit/${user.details}`}
                    >
                      Edit
                    </Link>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </>
  );
};

export default Todo;
