    let newBg = "red";
    setBg(newBg);
  };
  const [Name, setName] = useState("click me");
  const mal = "white";
  const [mg, darkBg] = useState(mal);
  const bgDark = () => {
    let darkBg = "yellow";
    setBg(darkBg);
    setName("ohhhhh");
