import React, { useState, useEffect } from "react";
import Axios from "axios";
import { Link } from "react-router-dom";
import { Anchor, Clipboard, PlusCircle, Edit2, X } from "react-feather";
const Todo = () => {
  const [resDetail, setResDetail] = useState({});

  const onSubmitDetails = () => {
    if (!details) return seterror("empty field");
    Axios.post("http://localhost:8080/todo/create", {
      details: details,
      user_id: window.localStorage.getItem("user_id"),
    }).then((res) => {
      console.log(res.data.data);
      setResDetail(res.data.data);
    });
  };
  const [details, setDetails] = useState("");
  const [error, seterror] = useState("");
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    Axios.get("http://localhost:8080/todo/getall", {
      user_id: window.localStorage.getItem("user_id"),
    }).then((res) => {
      console.log(res);
      if (res) setLoading(true);
    });
  });

  return loading ? (
    <>
      <div className="container-fluid">
        <div className="row ">
          <div className="col-xl-4"></div>
          <div className="col-xl-4">
            <div className="card mt-3">
              <div className="card-header">
                {" "}
                <h1>
                  <Clipboard
                    size="30"
                    class="mr-2"
                    style={{ verticalAlign: "baseline" }}
                  />
                  Things You Do
                  <Anchor
                    size="30"
                    class="mr-4"
                    style={{ verticalAlign: "baseline" }}
                  />
                </h1>
              </div>
              <div className="card-body">
                <div className="col-xl-12">
                  <input
                    type="text"
                    className="pl-2 border w-100"
                    style={{ height: "40px", outline: "none" }}
                    placeholder="What To Do"
                    onChange={(e) => setDetails(e.target.value)}
                  />
                </div>
                <div className="col-xl-12">
                  {" "}
                  <button
                    onClick={onSubmitDetails}
                    className="btn btn-outline bg-primary mt-4 text-white"
                  >
                    <PlusCircle
                      size="18"
                      className="mr-2"
                      style={{ verticalAlign: "sub" }}
                    />
                    Add
                  </button>
                </div>
              </div>
            </div>
            {/* {resDetail ? (
              <div className="alert alert-info">{resDetail.details}</div>
            ) : null} */}
            {resDetail.details ? (
              <div className="card mt-2">
                <div className="card-body">
                  <div className="container">
                    <div className="row">
                      <div className="col-sm-2 d-flex align-items-center">
                        <input type="checkbox"></input>
                      </div>
                      <div className="col-sm-7 d-flex align-items-center">
                        {resDetail.details}
                      </div>
                      <div className="col-sm-3">
                        <div className="btn btn-warning">
                          <Edit2 size="18" style={{ verticalAlign: "sub" }} />
                        </div>
                        <div className="btn btn-danger ml-2">
                          <X size="18" style={{ verticalAlign: "sub" }} />
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            ) : null}
          </div>
        </div>
      </div>
    </>
  ) : (
    <h1>LOADING..</h1>
  );
};

export default Todo;
