import React from "react";
import "./App.css";
import { useState } from "react";

function App() {
  const col = "purple";
  const [bg, setBg] = useState(col);
  const bgChange = () => {
    let newBg = "red";
    setBg(newBg);
  };
  const mal = "white";
  const [mg, darkBg] = useState(mal);
  const bgDark = () => {
    let darkBg = "yellow";
    setBg(darkBg);
  };

  return (
    <div style={{ backgroundColor: bg }}>
      <button onClick={bgChange} type="button">
        click me
      </button>

      <button onClick={bgDark} type="button">
        dark col
      </button>
    </div>
  );
}

export default App;
